  /**
   * Format a String to Number
   * @param string
   * @param decimal
   * @returns {string}
   */
  static formatStringNumber(string, decimal) {
    return  utils.formatStringNumber(string, decimal);
  }
  constructor(private datePipe: DatePipe,
              private userService: UserService,
              rendererFactory: RendererFactory2) {
    this.renderer = rendererFactory.createRenderer(null, null);
    this.authUser = this.userService.getAuthUser();
    // //console.log('user =', this.authUser);
  }

/**
   * This function helps to convert image from source folder. to base64
   * @param url
   * @param name
   */
  getBase64FromImageUrl(url, name, callback) {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    if (name === 'waterMark') {
      ctx.globalAlpha = 0.10;
    }
    const image = document.createElement('img');
    image.id = 'imageId';
    image.src = url;
    document.body.appendChild(image);
    // //console.log('document.body', document.body);
    canvas.width = image.width;
    canvas.height = image.height;
    return canvas.toDataURL();
  };


  /**
   * This is used to download csv for transaction
   * @param transactions
   * @param callback
   */
  transactionCsvDownload(transactions: Array<any>, callback) {
    const data = [];
    const name = `Transaction History at ${new Date().toTimeString()}.csv`;
    transactions.forEach((transaction) => {
      data.push(
        {
          Merchant_Name: transaction.merchant.trading_name,
          Mandate: transaction.mandate.account_name,
          Currency: transaction.currency.name,
          Transaction_Reference: transaction.transaction_reference,
          Status: (String(transaction.status) === '00')  ? 'PAID' : 'PENDING',
          Item_Amount: PdfService.formatStringNumber(transaction.amount, 2),
          Amount_Paid: PdfService.formatStringNumber(transaction.amount_paid, 2),
          Date_Log: this.datePipe.transform(transaction.created_at, 'MMMM dd, yyyy, h:mm a'),
          Date_Paid: this.datePipe.transform(transaction.date_paid, 'MMMM dd, yyyy, h:mm a')
        }
        );
       /*Date_Settled: this.datePipe.transform(transaction.date_settled, 'MMMM dd, yyyy'),
       Date_Reversed: this.datePipe.transform(transaction.date_reversed, 'MMMM dd, yyyy')*/
    });
    this.data = data;
    this.fileName = name;
    this.constructCsv(callback);
  }


